clcclose allclearVT = 200;thetaT = 180;thetaT = (thetaT/180)*pi;D0 = 37000;epsilon0 = 35;epsilon0 = (epsilon0/180)*pi;XT0 = D0*cos(epsilon0);YT0 = D0*sin(epsilon0);VT_hor = VT*cos(thetaT);VT_ver = VT*sin(thetaT);P = 4;VM = P*VT;XM0 = 0;YM0 = 0;XT = XT0;YT = YT0;xm = XM0;ym = YM0;thetaM = atan3((YT-ym),(XT-xm));VM_hor = VM*cos(thetaM);VM_ver = VM*sin(thetaM);dT = 0.1;epsilon = epsilon0;D = D0;figHandle = figure;grid onflag = 0;time = 0:dT:50;for i = 1:length(time):    XT = XT + VT_hor*dT;    YT = YT + VT_ver*dT;        D_dot = (VT*cos(epsilon - thetaT) - ...            VM*cos(thetaM - epsilon));%thetaM - epsilon +    Dn = D + D_dot * dT;         if abs(Dn) > abs(D)        flag = flag+1;    end    if flag == 2         break;    end        D = Dn;    epsilon_dot = (VT*sin(thetaT - epsilon) - ...                    VM*sin(thetaM - epsilon))/D;%thetaM - epsilon +     epsilon = epsilon + epsilon_dot * dT; % atan3((YT-ym),(XT-xm));    thetaM = epsilon;        VM_hor = VM*cos(thetaM);    VM_ver = VM*sin(thetaM);        xm = xm + VM_hor*dT;    ym = ym + VM_ver*dT;        hold on;    box on;    plot(XT,YT,'r*');        plot(xm,ym,'g*');        xlabel('X-coordinate (meters)');    ylabel('Y-coordinate (meters)');        end